import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import Datetime from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import './FuelTransactionForm.css'; // Assuming you have this CSS file

// Validation schema using Yup
const validationSchema = Yup.object({
  time: Yup.date().required('Please select the time'),
  quantity: Yup.number().required('Please enter the quantity').positive('Quantity must be positive'),
  pump: Yup.string().required('Please select the pump'),
  revenue: Yup.number().required('Please enter the revenue').positive('Revenue must be positive'),
  price: Yup.number().required('Please enter the price per liter').positive('Price must be positive'),
});

const FuelTransactionForm = () => {
  const initialValues = {
    time: '',
    quantity: '',
    pump: '',
    revenue: '',
    price: ''
  };

  const handleSubmit = (values, { setSubmitting, resetForm }) => {
    setTimeout(() => {
      alert('Transaction submitted successfully: ' + JSON.stringify(values, null, 2));
      setSubmitting(false);
      resetForm();
    }, 400);
  };

  return (
    <div className="form-container">
      <h2>Nhập giao dịch</h2>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        {({ setFieldValue, isSubmitting }) => (
          <Form>
            <div className="form-group">
              <label>Thời gian</label>
              <Field name="time">
                {({ field, form }) => (
                  <Datetime
                    value={field.value}
                    onChange={val => setFieldValue('time', val)}
                    dateFormat="DD/MM/YYYY"
                    timeFormat="HH:mm:ss"
                  />
                )}
              </Field>
              <ErrorMessage name="time" component="div" className="error-message" />
            </div>

            <div className="form-group">
              <label>Số lượng (lít)</label>
              <Field type="number" name="quantity" placeholder="Số lượng" className="form-input" />
              <ErrorMessage name="quantity" component="div" className="error-message" />
            </div>

            <div className="form-group">
              <label>Trụ</label>
              <Field as="select" name="pump" className="form-select">
                <option value="">Chọn trụ</option>
                <option value="Pump 1">Trụ 1</option>
                <option value="Pump 2">Trụ 2</option>
                <option value="Pump 3">Trụ 3</option>
              </Field>
              <ErrorMessage name="pump" component="div" className="error-message" />
            </div>

            <div className="form-group">
              <label>Doanh thu (VNĐ)</label>
              <Field type="number" name="revenue" placeholder="Doanh thu" className="form-input" />
              <ErrorMessage name="revenue" component="div" className="error-message" />
            </div>

            <div className="form-group">
              <label>Đơn giá (VNĐ/lít)</label>
              <Field type="number" name="price" placeholder="Đơn giá" className="form-input" />
              <ErrorMessage name="price" component="div" className="error-message" />
            </div>

            <button type="submit" className="submit-button" disabled={isSubmitting}>
              Cập nhật
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default FuelTransactionForm;
